mapper.select:
SELECT NVL(SUM(price * cartstock), 0) money 
FROM cart c, S_product s 
WHERE c.pno = s.pno and c.userid = #{userid}

SELECT COUNT(*)
FROM cart
WHERE userid = #{userid}
AND pno = #{pno}

mapper.update:
UPDATE cart
SET cartstock = cartstock + #{cartstock}
WHERE userid = #{userid}
AND pno = #{pno}

UPDATE cart
SET cartstock = #{cartstock}
WHERE userid = #{userid}
AND pno = #{pno}

mapper.delete:
DELETE 
FROM wish
WHERE userid = #{userid}
AND pno = #{pno}

mapper.select:
SELECT COUNT(*)
FROM wish
WHERE userid = #{userid}
AND pno = #{pno}
